import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "../api/axiosConfig";
import "../styles/Post.css";
import Layout from "../components/Layout";

function PostListing() {
  const [user, setUser] = useState(null);
  const [universities, setUniversities] = useState([]);
  const [formData, setFormData] = useState({
    university_id: "",
    title: "",
    building_name: "",
    building_type: "Apartment",
    address: "",
    description: "",
    bedrooms: 1,
    bathrooms: 1,
    monthly_rent: "",
    security_deposit: "",
    lease_type: "lease",
    lease_duration: 1,
    sq_feet: "",
    amenities: "",
    fees_policies: "",
    contact_email: "",
    contact_mobile: "",
  });
  const [photos, setPhotos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("currentUser"));
    if (storedUser) {
      setUser(storedUser);
      axiosInstance
        .get("/api/universities")
        .then((response) => setUniversities(response.data))
        .catch((error) => console.error("Error fetching universities:", error));
    } else {
      navigate("/"); // Redirect to login if no user found
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setPhotos((prev) => [...prev, ...files]);
  };

  const removePhoto = (index) => {
    setPhotos((prev) => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.address.trim()) {
      alert("Address is required!");
      return;
    }
    const listingData = {
      ...formData,
      user_id: user.id,
      availability: "available",
    };
    const formDataToSend = new FormData();
    Object.entries(listingData).forEach(([key, value]) => {
      formDataToSend.append(key, value);
    });
    photos.forEach((photo) => formDataToSend.append("photos", photo));

    try {
      const response = await axiosInstance.post(
        "/api/listings",
        formDataToSend,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );
      alert("Listing created successfully!");
      navigate(`/listings/${response.data.id}`);
    } catch (error) {
      console.error("Error creating listing:", error);
      alert("Failed to create listing. Please try again.");
    }
  };

  return (
    <Layout>
      <div className="post-listing-container">
        <h1 className="title">Create a Listing</h1>
        <form className="post-listing-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>University</label>
            <select
              name="university_id"
              value={formData.university_id}
              onChange={handleInputChange}
              required
            >
              <option value="">Select University</option>
              {universities.map((uni) => (
                <option key={uni.id} value={uni.id}>
                  {uni.name}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Title</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Building Name</label>
            <input
              type="text"
              name="building_name"
              value={formData.building_name}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Building Type</label>
            <select
              name="building_type"
              value={formData.building_type}
              onChange={handleInputChange}
            >
              <option>House</option>
              <option>Apartment</option>
              <option>Studio</option>
              <option>Condo</option>
              <option>Townhome</option>
            </select>
          </div>
          <div className="form-group">
            <label>Address</label>
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleInputChange}
              required
              placeholder="Enter the address"
            />
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              required
            ></textarea>
          </div>
          <div className="form-group">
            <label>Bedrooms</label>
            <select
              name="bedrooms"
              value={formData.bedrooms}
              onChange={handleInputChange}
            >
              {[...Array(10).keys()].map((n) => (
                <option key={n + 1} value={n + 1}>
                  {n + 1}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Bathrooms</label>
            <select
              name="bathrooms"
              value={formData.bathrooms}
              onChange={handleInputChange}
            >
              {[...Array(10).keys()].map((n) => (
                <option key={n + 1} value={n + 1}>
                  {n + 1}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Monthly Rent</label>
            <input
              type="number"
              name="monthly_rent"
              value={formData.monthly_rent}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Security Deposit</label>
            <input
              type="number"
              name="security_deposit"
              value={formData.security_deposit}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Lease Type</label>
            <select
              name="lease_type"
              value={formData.lease_type}
              onChange={handleInputChange}
            >
              <option>lease</option>
              <option>sublease</option>
            </select>
          </div>
          <div className="form-group">
            <label>Lease Duration (Months)</label>
            <select
              name="lease_duration"
              value={formData.lease_duration}
              onChange={handleInputChange}
            >
              {[...Array(12).keys()].map((n) => (
                <option key={n + 1} value={n + 1}>
                  {n + 1}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Square Feet</label>
            <input
              type="number"
              name="sq_feet"
              value={formData.sq_feet}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Amenities</label>
            <input
              type="text"
              name="amenities"
              value={formData.amenities}
              onChange={handleInputChange}
              placeholder="AC, Parking"
            />
          </div>
          <div className="form-group">
            <label>Fees & Policies</label>
            <textarea
              name="fees_policies"
              value={formData.fees_policies}
              onChange={handleInputChange}
              required
            ></textarea>
          </div>
          <div className="form-group">
            <label>Contact Email</label>
            <input
              type="email"
              name="contact_email"
              value={formData.contact_email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Contact Mobile</label>
            <input
              type="tel"
              name="contact_mobile"
              value={formData.contact_mobile}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Upload Photos</label>
            <input type="file" multiple onChange={handleFileChange} />
            <div className="photo-preview">
              {photos.map((photo, index) => (
                <div key={index} className="photo-item">
                  <span>{photo.name}</span>
                  <button
                    type="button"
                    onClick={() => removePhoto(index)}
                    className="remove-photo"
                  >
                    Remove
                  </button>
                </div>
              ))}
            </div>
          </div>
          <button type="submit" className="btn-submit">
            Post Listing
          </button>
        </form>
      </div>
    </Layout>
  );
}

export default PostListing;
