import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosConfig';
import '../styles/ListingDetails.css';
import Layout from '../components/Layout'; // Use your existing Layout component
import 'bootstrap/dist/css/bootstrap.min.css';

function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    axiosInstance
      .get(`/api/listings/${id}`)
      .then((response) => {
        setListing(response.data.listing);
        setPhotos(response.data.photos);
        setUser(response.data.user);
      })
      .catch((error) => {
        console.error('Error fetching listing details:', error);
        navigate('/'); // Redirect to home if fetching fails
      });
  }, [id, navigate]);

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? photos.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === photos.length - 1 ? 0 : prevIndex + 1
    );
  };

  const calculateDuration = (start, end) => {
    const startDate = new Date(start);
    const endDate = new Date(end);
    const diffMonths =
      endDate.getMonth() -
      startDate.getMonth() +
      12 * (endDate.getFullYear() - startDate.getFullYear());
    return diffMonths === 1 ? `${diffMonths} Month` : `${diffMonths} Months`;
  };

  if (!listing) return <p>Loading...</p>;

  return (
    <Layout>
      {/* Main Content */}
      <div className="container-fluid mt-5 pt-3">
        {/* Image Carousel */}
        <div className="image-carousel-container mx-auto">
          <div className="position-relative">
            {photos.length > 0 ? (
              <>
                <button
                  className="carousel-btn btn btn-dark position-absolute top-50 start-0 translate-middle-y"
                  onClick={handlePrev}
                >
                  &#8249;
                </button>
                <img
                  src={`http://localhost:9000${photos[currentImageIndex].photo_url}`}
                  alt={`Listing ${currentImageIndex + 1}`}
                  className="img-fluid rounded"
                />
                <button
                  className="carousel-btn btn btn-dark position-absolute top-50 end-0 translate-middle-y"
                  onClick={handleNext}
                >
                  &#8250;
                </button>
                <div className="photo-count bg-dark text-white text-center p-2 mt-2 rounded position-absolute bottom-0 end-0">
                  {currentImageIndex + 1} / {photos.length} Photos
                </div>
              </>
            ) : (
              <p className="text-center">No images available</p>
            )}
          </div>
        </div>

        {/* Details Section */}
        <div className="details-section container mt-4">
          <h1>${listing.monthly_rent}/mo</h1>
          <h2>{listing.title}</h2>
          <p>
            {listing.address}{' '}
            {listing.building_name && `| ${listing.building_name}`}
          </p>
          <p>
            {listing.building_type} | {listing.bedrooms} Bed |{' '}
            {listing.bathrooms} Bath |{' '}
            {listing.availability ? 'Available' : 'Not Available'}
          </p>
          <hr />
          <h5>Overview</h5>
          <p>{listing.description}</p>
          <hr />
          <h5>Amenities</h5>
          <ul>
            {listing.amenities.split(',').map((amenity, index) => (
              <li key={index}>{amenity.trim()}</li>
            ))}
          </ul>
          <hr />
          <h5>Fees and Policies</h5>
          <p>{listing.fees_policies}</p>
          <hr />
          <h5>Leasing Terms</h5>
          <p>Duration: {calculateDuration(listing.lease_from, listing.lease_to)}</p>
          <p>Security Deposit: ${listing.security_deposit}</p>
          <hr />
          <h5>Contact</h5>
          <p>
            <strong>Name:</strong> {user?.name || 'N/A'}
          </p>
          <p>
            <strong>Email:</strong> {listing.contact_email}
          </p>
          <p>
            <strong>Mobile:</strong> {listing.contact_mobile}
          </p>
          <button
            className="btn btn-primary"
            onClick={() => {
              // Implement messaging functionality
            }}
          >
            Message
          </button>
        </div>
      </div>
    </Layout>
  );
}

export default ListingDetails;
