import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axiosInstance from '../api/axiosConfig';
import '../styles/ListingDetailsPage.css';

function ListingDetailsPage() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);

  useEffect(() => {
    axiosInstance.get(`/api/listings/${id}`)
      .then(response => setListing(response.data))
      .catch(error => console.error(error));
  }, [id]);

  if (!listing) return <p>Loading...</p>;

  return (
    <div className="listing-details">
      <h1>{listing.title}</h1>
      <p>{listing.description}</p>
      <img src={listing.photo_url || '/placeholder.jpg'} alt={listing.title} />
      <p><strong>Monthly Rent:</strong> ${listing.monthly_rent}</p>
      <p><strong>Address:</strong> {listing.address}</p>
      <p><strong>Amenities:</strong> {listing.amenities}</p>
    </div>
  );
}

export default ListingDetailsPage;
