import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosConfig';
import Layout from '../components/Layout';
import '../styles/ListingDetails.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    axiosInstance
      .get(`/api/listings/${id}`)
      .then((response) => {
        setListing(response.data.listing);
        setPhotos(response.data.photos);
        setUser(response.data.user);
      })
      .catch((error) => {
        console.error('Error fetching listing details:', error);
        navigate('/'); // Redirect to home if fetching fails
      });
  }, [id, navigate]);

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? photos.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === photos.length - 1 ? 0 : prevIndex + 1
    );
  };

  const calculateDuration = (start, end) => {
    const startDate = new Date(start);
    const endDate = new Date(end);
    const diffMonths =
      endDate.getMonth() -
      startDate.getMonth() +
      12 * (endDate.getFullYear() - startDate.getFullYear());
    return diffMonths === 1 ? `${diffMonths} Month` : `${diffMonths} Months`;
  };

  if (!listing) return <p>Loading...</p>;

  return (
    <Layout>
      {/* Main Content */}
      <div className="container-fluid mt-5 pt-5">
        {/* Image Carousel */}
        <div className="image-carousel position-relative">
          {photos.length > 0 ? (
            <>
              <button
                className="carousel-btn btn btn-dark position-absolute top-50 start-0 translate-middle-y"
                onClick={handlePrev}
              >
                &#8249;
              </button>
              <img
                src={`http://localhost:9000${photos[currentImageIndex].photo_url}`}
                alt={`Listing ${currentImageIndex + 1}`}
                className="img-fluid w-100"
              />
              <button
                className="carousel-btn btn btn-dark position-absolute top-50 end-0 translate-middle-y"
                onClick={handleNext}
              >
                &#8250;
              </button>
              <div className="photo-count bg-dark text-white position-absolute bottom-0 end-0 p-2">
                {currentImageIndex + 1} / {photos.length} Photos
              </div>
            </>
          ) : (
            <p className="text-center">No images available</p>
          )}
        </div>

        {/* Tabs */}
        <ul className="nav nav-tabs mt-3" id="listingTabs" role="tablist">
          <li className="nav-item">
            <button
              className="nav-link active"
              id="overview-tab"
              data-bs-toggle="tab"
              data-bs-target="#overview"
              type="button"
              role="tab"
              aria-controls="overview"
              aria-selected="true"
            >
              Overview
            </button>
          </li>
          <li className="nav-item">
            <button
              className="nav-link"
              id="amenities-tab"
              data-bs-toggle="tab"
              data-bs-target="#amenities"
              type="button"
              role="tab"
              aria-controls="amenities"
              aria-selected="false"
            >
              Amenities
            </button>
          </li>
          <li className="nav-item">
            <button
              className="nav-link"
              id="fees-tab"
              data-bs-toggle="tab"
              data-bs-target="#fees"
              type="button"
              role="tab"
              aria-controls="fees"
              aria-selected="false"
            >
              Fees & Policies
            </button>
          </li>
        </ul>

        <div className="tab-content mt-3" id="listingTabsContent">
          {/* Overview Tab */}
          <div
            className="tab-pane fade show active"
            id="overview"
            role="tabpanel"
            aria-labelledby="overview-tab"
          >
            <h1>${listing.monthly_rent}/mo</h1>
            <h2>{listing.title}</h2>
            <p>
              {listing.address}{' '}
              {listing.building_name && `| ${listing.building_name}`}
            </p>
            <p>
              {listing.building_type} | {listing.bedrooms} Bed |{' '}
              {listing.bathrooms} Bath |{' '}
              {listing.availability ? 'Available' : 'Not Available'}
            </p>
            <hr />
            <p>{listing.description}</p>
          </div>

          {/* Amenities Tab */}
          <div
            className="tab-pane fade"
            id="amenities"
            role="tabpanel"
            aria-labelledby="amenities-tab"
          >
            <h5>Amenities</h5>
            <ul>
              {listing.amenities.split(',').map((amenity, index) => (
                <li key={index}>{amenity.trim()}</li>
              ))}
            </ul>
          </div>

          {/* Fees & Policies Tab */}
          <div
            className="tab-pane fade"
            id="fees"
            role="tabpanel"
            aria-labelledby="fees-tab"
          >
            <h5>Fees & Policies</h5>
            <p>{listing.fees_policies}</p>
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default ListingDetails;
