import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosConfig';
import '../styles/Dashboard.css';

function Dashboard() {
  const [universityListings, setUniversityListings] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user info from localStorage
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);

      // Fetch listings based on the user's university
      axiosInstance.get(`/api/listings/university?university_id=${parsedUser.university_id}`)
        .then((response) => setUniversityListings(response.data.slice(0, 3))) // Get top 3 listings
        .catch((error) => console.error('Error fetching listings:', error));
    } else {
      navigate('/'); // Redirect to login if no user found
    }
  }, [navigate]);

  const firstName = user?.name?.split(' ')[0] || 'Guest';

  return (
    <div className="dashboard">
      <div className="welcome-section">
        <h1 className="welcome-title">Welcome to FindMyNest</h1>
        <p className="personalized-greeting">Hello, {firstName}! Here's what's new today:</p>
      </div>

      <div className="things-to-consider">
        <h2>Things to Consider Before Renting:</h2>
        <ul>
          <li>Inspect the property thoroughly before signing.</li>
          <li>Check the neighborhood for convenience and safety.</li>
          <li>Understand the lease agreement fully.</li>
          <li>Budget for rent, utilities, and extra costs.</li>
          <li>Ensure all amenities are functional before moving in.</li>
        </ul>
      </div>

      <div className="recent-listings">
        <h2>Most Recent Listings near {user?.university_id || 'Your University'}:</h2>
        <div className="listings-grid">
          {universityListings.map((listing) => (
            <div key={listing.id} className="listing-card" onClick={() => navigate(`/listings/${listing.id}`)}>
              <img src={listing.photo_url || '/placeholder.jpg'} alt={listing.title} className="listing-image" />
              <div className="listing-info">
                <h3>{listing.title}</h3>
                <p>{listing.address}</p>
                <p><strong>${listing.monthly_rent}/mo</strong></p>
                <p>{listing.bedrooms} bd | {listing.bathrooms} ba</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
