import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosConfig';
import Layout from '../components/Layout';
import '../styles/ListingDetails.css';

function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch listing details by ID
    axiosInstance
      .get(`/api/listings/${id}`)
      .then((response) => {
        setListing(response.data.listing);
        setPhotos(response.data.photos); // Store photos array
        setUser(response.data.user);
      })
      .catch((error) => {
        console.error('Error fetching listing details:', error);
        navigate('/'); // Redirect to home if fetching fails
      });
  }, [id, navigate]);

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? photos.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === photos.length - 1 ? 0 : prevIndex + 1
    );
  };

  const calculateDuration = (start, end) => {
    const startDate = new Date(start);
    const endDate = new Date(end);
    const diffMonths =
      endDate.getMonth() -
      startDate.getMonth() +
      12 * (endDate.getFullYear() - startDate.getFullYear());
    return diffMonths === 1 ? `${diffMonths} Month` : `${diffMonths} Months`;
  };

  if (!listing) return <p>Loading...</p>;

  return (
    <Layout>
      <div className="listing-details-container">
        {/* Image Slider */}
        <div className="slider-container">
          <div className="slider">
            {photos && photos.length > 0 ? (
              <>
                <button className="arrow left" onClick={handlePrev}>
                  &#8249;
                </button>
                <img
                  src={`http://localhost:9000${photos[currentImageIndex].photo_url}`}
                  alt={`Listing ${currentImageIndex + 1}`}
                  className="slider-image"
                />
                <button className="arrow right" onClick={handleNext}>
                  &#8250;
                </button>
              </>
            ) : (
              <p>No images available</p>
            )}

            <div className="overlay-details">
              <h1>${listing.monthly_rent}/mo</h1>
              <h2>{listing.title}</h2>
              <p>
                {listing.address} {listing.building_name ? `| ${listing.building_name}` : ''}
              </p>
              <p>
                {listing.building_type} | {listing.bedrooms} Bed | {listing.bathrooms} Bath |{' '}
                {listing.availability ? 'Available' : 'Not Available'}
              </p>
            </div>
          </div>
        </div>

        {/* Overview Section */}
        <div className="listing-section">
          <h3>Overview</h3>
          <p>{listing.description}</p>
        </div>

        {/* Amenities Section */}
        <div className="listing-section">
          <h3>Amenities</h3>
          <ul>
            {listing.amenities.split(',').map((amenity, index) => (
              <li key={index}>{amenity.trim()}</li>
            ))}
          </ul>
        </div>

        {/* Fees and Policies Section */}
        <div className="listing-section">
          <h3>Fees and Policies</h3>
          <p>{listing.fees_policies}</p>
        </div>

        {/* Leasing Terms Section */}
        <div className="listing-section">
          <h3>Leasing Terms</h3>
          <p>Lease Duration: {calculateDuration(listing.lease_from, listing.lease_to)}</p>
          <p>Security Deposit: ${listing.security_deposit}</p>
        </div>

        {/* Contact Section */}
        <div className="listing-section contact-card">
          <h3>Contact</h3>
          <p>
            <strong>Name:</strong> {user?.name || 'N/A'}
          </p>
          <p>
            <strong>Email:</strong> {listing.contact_email}
          </p>
          <p>
            <strong>Mobile:</strong> {listing.contact_mobile}
          </p>
          <button
            className="message-button"
            onClick={() => {
              // Implement message redirect within app
            }}
          >
            Message
          </button>
        </div>
      </div>
    </Layout>
  );
}

export default ListingDetails;
