import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosConfig';
import '../styles/ListingDetails.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    axiosInstance
      .get(`/api/listings/${id}`)
      .then((response) => {
        setListing(response.data.listing);
        setPhotos(response.data.photos);
        setUser(response.data.user);
      })
      .catch((error) => {
        console.error('Error fetching listing details:', error);
        navigate('/'); // Redirect to home if fetching fails
      });
  }, [id, navigate]);

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? photos.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === photos.length - 1 ? 0 : prevIndex + 1
    );
  };

  const calculateDuration = (start, end) => {
    const startDate = new Date(start);
    const endDate = new Date(end);
    const diffMonths =
      endDate.getMonth() -
      startDate.getMonth() +
      12 * (endDate.getFullYear() - startDate.getFullYear());
    return diffMonths === 1 ? `${diffMonths} Month` : `${diffMonths} Months`;
  };

  if (!listing) return <p>Loading...</p>;

  return (
    <div className="listing-details">
      {/* Fixed Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
        <div className="container">
          <a className="navbar-brand" href="/">
            FindMyNest
          </a>
        </div>
      </nav>

      {/* Main Content */}
      <div className="container-fluid mt-5 pt-5">
        {/* Image Carousel */}
        <div className="image-carousel position-relative">
          {photos.length > 0 ? (
            <>
              <button
                className="carousel-btn btn btn-dark position-absolute top-50 start-0 translate-middle-y"
                onClick={handlePrev}
              >
                &#8249;
              </button>
              <img
                src={`http://localhost:9000${photos[currentImageIndex].photo_url}`}
                alt={`Listing ${currentImageIndex + 1}`}
                className="img-fluid w-100"
              />
              <button
                className="carousel-btn btn btn-dark position-absolute top-50 end-0 translate-middle-y"
                onClick={handleNext}
              >
                &#8250;
              </button>
              <div className="photo-count bg-dark text-white position-absolute bottom-0 end-0 p-2">
                {currentImageIndex + 1} / {photos.length} Photos
              </div>
            </>
          ) : (
            <p className="text-center">No images available</p>
          )}
        </div>

        {/* Tabs Section */}
        <ul className="nav nav-tabs mt-4" id="listingDetailsTabs" role="tablist">
          <li className="nav-item" role="presentation">
            <button
              className="nav-link active"
              id="overview-tab"
              data-bs-toggle="tab"
              data-bs-target="#overview"
              type="button"
              role="tab"
              aria-controls="overview"
              aria-selected="true"
            >
              Overview
            </button>
          </li>
          <li className="nav-item" role="presentation">
            <button
              className="nav-link"
              id="amenities-tab"
              data-bs-toggle="tab"
              data-bs-target="#amenities"
              type="button"
              role="tab"
              aria-controls="amenities"
              aria-selected="false"
            >
              Amenities
            </button>
          </li>
          <li className="nav-item" role="presentation">
            <button
              className="nav-link"
              id="fees-policies-tab"
              data-bs-toggle="tab"
              data-bs-target="#fees-policies"
              type="button"
              role="tab"
              aria-controls="fees-policies"
              aria-selected="false"
            >
              Fees & Policies
            </button>
          </li>
        </ul>

        <div className="tab-content" id="listingDetailsTabsContent">
          {/* Overview Section */}
          <div
            className="tab-pane fade show active"
            id="overview"
            role="tabpanel"
            aria-labelledby="overview-tab"
          >
            <div className="details-section container mt-4">
              <h1>${listing.monthly_rent}/mo</h1>
              <h2>{listing.title}</h2>
              <p>
                {listing.address}{' '}
                {listing.building_name && `| ${listing.building_name}`}
              </p>
              <p>
                {listing.building_type} | {listing.bedrooms} Bed |{' '}
                {listing.bathrooms} Bath |{' '}
                {listing.availability ? 'Available' : 'Not Available'}
              </p>
              <hr />
              <h5>Overview</h5>
              <p>{listing.description}</p>
            </div>
          </div>

          {/* Amenities Section */}
          <div
            className="tab-pane fade"
            id="amenities"
            role="tabpanel"
            aria-labelledby="amenities-tab"
          >
            <div className="details-section container mt-4">
              <h5>Amenities</h5>
              <ul>
                {listing.amenities
                  ? listing.amenities.split(',').map((amenity, index) => (
                      <li key={index}>{amenity.trim()}</li>
                    ))
                  : 'No amenities available'}
              </ul>
            </div>
          </div>

          {/* Fees & Policies Section */}
          <div
            className="tab-pane fade"
            id="fees-policies"
            role="tabpanel"
            aria-labelledby="fees-policies-tab"
          >
            <div className="details-section container mt-4">
              <h5>Fees & Policies</h5>
              <p>{listing.fees_policies || 'No fees or policies available'}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ListingDetails;
