import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "../api/axiosConfig";
import "../styles/MyListings.css";
import Layout from "../components/Layout";

function MyListings() {
  const [listings, setListings] = useState([]);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("currentUser"));

  useEffect(() => {
    if (!user) {
      window.location.href = "/"; // Redirect to login if no user found
    } else {
      fetchListings();
    }
  }, []);

  const fetchListings = async () => {
    try {
      const response = await axiosInstance.get(`/api/listings?user_id=${user.id}`);
      setListings(response.data);
    } catch (error) {
      console.error("Error fetching listings:", error);
    }
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this listing?");
    if (confirmDelete) {
      try {
        await axiosInstance.delete(`/api/listings/${id}`);
        alert("Listing deleted successfully!");
        fetchListings(); // Refresh the listings
      } catch (error) {
        console.error("Error deleting listing:", error);
        alert("Failed to delete listing. Please try again.");
      }
    }
  };

  const handleEdit = (id) => {
    navigate(`/edit-listing/${id}`); // Redirect to the Edit Listing page
  };

  return (
    <Layout>
      <div className="my-listings-container">
        <h1 className="title">My Listings</h1>
        {listings.length > 0 ? (
          <div className="listings-grid">
            {listings.map((listing) => (
              <div key={listing.id} className="listing-card">
                <img
                  src={
                    listing.photos && listing.photos.length > 0
                      ? `http://localhost:9000${listing.photos[0].photo_url}`
                      : "/placeholder.jpg"
                  }
                  alt={listing.title}
                  className="listing-image"
                />
                <div className="listing-info">
                  <h3>{listing.title}</h3>
                  <p>
                    {listing.address} | ${listing.monthly_rent}/mo
                  </p>
                  <p>
                    {listing.bedrooms} Bed | {listing.bathrooms} Bath
                  </p>
                  <div className="listing-actions">
                    <button className="btn-edit" onClick={() => handleEdit(listing.id)}>
                      Edit
                    </button>
                    <button className="btn-delete" onClick={() => handleDelete(listing.id)}>
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="no-listings">You have not posted any listings yet.</p>
        )}
      </div>
    </Layout>
  );
}

export default MyListings;
